import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { Card, CardContent, Button, Link } from "@mui/material";
import { styled } from "@mui/material/styles";
import DownloadOutlined from "@mui/icons-material/DownloadOutlined";

// Assuming you have a way to get the current block number dynamically, this could be done via an API call
const CurrentBlockNumber = ({ blockNumber }) => (
    <Box sx={{ fontWeight: "bold", fontSize: "2rem", color: "#6d8d6e", textAlign: "center", mb: 2 }}>
        Current Block: {blockNumber || "Loading..."}
    </Box>
);

const Square = ({ language }) => {
    return (
        <Link href="/path-to-your-whitepaper.pdf" target="_blank" underline="none" download>
            <Box
                sx={{
                    border: "solid 3px #b0946e",
                    color: "#6d8d6e",
                    mx: { sm: 2, xs: 1 },
                    p: 3,
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                }}
            >
                <DownloadOutlined fontSize="large" />
                {language}
            </Box>
        </Link>
    );
};

const NetworkActivitySection = ({ blockNumber }) => {
    return (
        <Box sx={{ py: 7, bgcolor: "white" }} id="networkactivitysection">
            <Grid container spacing={4} sx={{ width: "100%", mx: 0 }}>
                {/* Left Side: Network Stats */}
                <Grid item xs={12} md={6} sx={{ px: 3 }}>
                    <Card sx={{ minWidth: 275, maxWidth: 600, mx: "auto", bgcolor: "#b29874", color: "#363636", py: 3 }}>
                        <CardContent sx={{ textAlign: "center" }}>
                            {/* Live Network Stats */}
                            <Typography variant="h4" sx={{ fontWeight: "bold", mb: 3 }}>
                                Network Activity
                            </Typography>
                            <CurrentBlockNumber blockNumber={blockNumber} />
                            <Typography variant="body1" color="textSecondary">
                                Track the latest block number and stay updated with real-time information about the Alkebuleum blockchain.
                            </Typography>

                            <Box sx={{ mt: 3 }}>
                                <Button
                                    component="a"
                                    href="https://your-block-explorer-url" // Replace with actual block explorer URL
                                    target="_blank"
                                    sx={{ color: "white", bgcolor: "#6d8d6e", mt: 3, px: 3 }}
                                >
                                    View Block Explorer
                                </Button>
                            </Box>
                        </CardContent>
                    </Card>
                </Grid>

                {/* Right Side: Whitepaper Download */}
                <Grid item xs={12} md={6} sx={{ px: 4 }}>
                    <Box sx={{ typography: { sm: "h4", xs: "h5" }, mb: 2, fontWeight: "bold !important", color: "#363636" }}>
                        Download Whitepaper
                    </Box>

                    <Typography variant="body1" color="textSecondary" component="div">
                        Discover how Alkebuleum is reshaping blockchain technology by addressing real-world challenges. Learn about its
                        vision, technology, and economic impact by downloading our whitepaper.
                    </Typography>

                    <Box sx={{ display: "flex", justifyContent: "center", mt: 3 }}>
                        <Square language="English" />
                        <Square language="French" />
                        <Square language="Swahili" />
                    </Box>
                </Grid>
            </Grid>
        </Box>
    );
};

export default NetworkActivitySection;
