import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import Container from "@mui/material/Container";
import CurrencyBitcoinIcon from "@mui/icons-material/CurrencyBitcoin";
import TravelExploreIcon from "@mui/icons-material/TravelExplore";
import AppRegistrationIcon from "@mui/icons-material/AppRegistration";

const HeroSection = () => {
    return (
        <Box sx={{ py: 8, position: "relative" }} id="herosection">
            {/* Background Image */}
            <Box
                component="img"
                src="/back.png"  // Path to the public folder image
                alt="Hero Background"
                sx={{
                    width: "100%",
                    height: { xs: "300px", sm: "500px" },
                    objectFit: "cover",
                    objectPosition: "center",
                    mb: 4,
                }}
            />

            <Box sx={{ maxWidth: "600px", py: { sm: 5, xs: 2 }, mb: { sm: 5, xs: 2 }, mx: "auto", textAlign: "center" }}>
                {/* ALKEBULEUM Title */}
                <Box sx={{ color: "#363636", fontWeight: "bold", typography: { sm: "h2", xs: "h5" } }} component="div">
                    ALKEBULEUM
                </Box>

                {/* Description */}
                <Box sx={{ color: "#363636", mb: 3, px: 4, typography: { sm: "h5", xs: "body" } }} component="div">
                    Pan-African blockchain protocol revolutionizing institutional transparency and powering the cryptocurrency Alke (AKE)
                </Box>

                {/* List of Square Buttons */}
                <Stack direction="row" spacing={2} justifyContent="center" sx={{ mt: 4 }}>
                    {/* Button 1: Get AKE */}
                    <Box
                        sx={{
                            display: "flex",
                            flexDirection: "column",
                            alignItems: "center",
                            borderRadius: "16px",
                            backgroundColor: "#f5f5f5",
                            padding: 2,
                            width: 150,
                            height: 150,
                            textAlign: "center",
                            boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
                        }}
                    >
                        <CurrencyBitcoinIcon sx={{ fontSize: 40, color: "#6d8d6e", mb: 1 }} />
                        <Typography variant="body1" fontWeight="bold">
                            Get AKE
                        </Typography>
                    </Box>

                    {/* Button 2: Block Explorer */}
                    <Box
                        sx={{
                            display: "flex",
                            flexDirection: "column",
                            alignItems: "center",
                            borderRadius: "16px",
                            backgroundColor: "#f5f5f5",
                            padding: 2,
                            width: 150,
                            height: 150,
                            textAlign: "center",
                            boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
                        }}
                    >
                        <TravelExploreIcon sx={{ fontSize: 40, color: "#6d8d6e", mb: 1 }} />
                        <Typography variant="body1" fontWeight="bold">
                            Block Explorer
                        </Typography>
                    </Box>

                    {/* Button 3: Try Apps */}
                    <Box
                        sx={{
                            display: "flex",
                            flexDirection: "column",
                            alignItems: "center",
                            borderRadius: "16px",
                            backgroundColor: "#f5f5f5",
                            padding: 2,
                            width: 150,
                            height: 150,
                            textAlign: "center",
                            boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
                        }}
                    >
                        <AppRegistrationIcon sx={{ fontSize: 40, color: "#6d8d6e", mb: 1 }} />
                        <Typography variant="body1" fontWeight="bold">
                            Try Apps
                        </Typography>
                    </Box>
                </Stack>
            </Box>
        </Box>
    );
};

export default HeroSection;
