import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import { Card, CardContent, Button } from "@mui/material";
import { styled } from "@mui/material/styles";
import DownloadOutlined from "@mui/icons-material/DownloadOutlined";

// You can import the cover image for the Dapp here if you have one
// import coverImage from "../../assets/images/cover-image.jpg";

const FeaturesSection = () => {
    return (
        <Box sx={{ py: 7, border: "none", bgcolor: "white" }} id="featuressection">
            <Grid container spacing={4} sx={{ width: "100%", mx: 0 }}>
                <Grid item xs={12} md={6} sx={{ px: 3 }}>
                    {/* Card for promoting Openbook Dapp */}
                    <Card sx={{ minWidth: 275, maxWidth: 600, mx: "auto", bgcolor: "#b29874", color: "#363636", py: 3 }}>
                        <CardContent sx={{ textAlign: "center" }}>
                            <Box sx={{ fontWeight: "bold", mb: 3, typography: { sm: "h4", xs: "h5" } }} component="div">
                                Create Your Decentralized Community
                            </Box>
                            {/* Add a cover image for Openbook if you have one */}
                            {/* <Box component="img" src={coverImage} alt="Openbook Cover" sx={{ maxWidth: "100%", height: "auto", mb: 3 }} /> */}

                            <Typography variant="body1" color="#363636" component="div" sx={{ mb: 3 }}>
                                Openbook is your gateway to the Alkebuleum blockchain. Use it to create decentralized workspaces where
                                groups and organizations can collaborate, build projects, and grow together in a transparent and secure environment.
                            </Typography>

                            <Button
                                component="a"
                                href="/signup" // replace with your actual signup link
                                sx={{ color: "white", bgcolor: "#6d8d6e", mt: 3, px: 3 }}
                            >
                                Sign Up to Create Your Community
                            </Button>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid item xs={12} md={6} sx={{ px: 4 }}>
                    {/* Additional information about Openbook or another feature */}
                    <Box sx={{ color: "#363636", typography: { sm: "h4", xs: "h5" }, mb: 2, fontWeight: "bold !important" }}>
                        How Openbook Works
                    </Box>

                    <Typography variant="body1" color="#363636" component="div">
                        With Openbook, users can easily create a decentralized community, manage tasks, and collaborate on projects. The platform ensures transparency, security, and community-driven growth through blockchain technology.
                    </Typography>

                    <Typography variant="body1" color="#363636" component="div" sx={{ mt: 2 }}>
                        Sign up now to start building your decentralized community and be a part of the future of collaborative blockchain workspaces.
                    </Typography>
                </Grid>
            </Grid>
        </Box>
    );
};

export default FeaturesSection;
