import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { Card, CardContent, Button } from "@mui/material";
import { styled } from "@mui/material/styles";
import openbookArt from "../../assets/images/openbookArt.png"; // Importing your image

const FeaturesSection = () => {
    return (
        <Box sx={{ py: 10, bgcolor: "white" }} id="featuressection">
            <Container maxWidth="lg">
                <Grid container spacing={5} alignItems="center" justifyContent="center">
                    {/* Left Side: Image Section */}
                    <Grid item xs={12} md={6}>
                        <Box
                            component="img"
                            src={openbookArt}
                            alt="Openbook Art"
                            sx={{
                                width: "100%",
                                height: "auto",
                                borderRadius: "8px",
                                boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
                            }}
                        />
                    </Grid>

                    {/* Right Side: Text and Call-to-Action Section */}
                    <Grid item xs={12} md={6}>
                        <Card
                            elevation={3}
                            sx={{
                                backgroundColor: "#f5f5f5",
                                color: "#333",
                                borderRadius: "12px",
                                padding: "24px",
                            }}
                        >
                            <CardContent>
                                {/* Section Title */}
                                <Typography
                                    variant="h4"
                                    component="h2"
                                    sx={{ fontWeight: "bold", mb: 3, textAlign: "center" }}
                                >
                                    Create Your Decentralized Community with Openbook
                                </Typography>

                                {/* Description */}
                                <Typography
                                    variant="body1"
                                    color="textSecondary"
                                    sx={{ mb: 4, textAlign: "center", lineHeight: 1.7 }}
                                >
                                    Openbook is your gateway to the Alkebuleum blockchain. Use it to create decentralized
                                    workspaces where groups and organizations can collaborate, build projects, and grow together
                                    in a transparent and secure environment.
                                </Typography>

                                {/* Call-to-Action Button */}
                                <Box sx={{ textAlign: "center" }}>
                                    <Button
                                        component="a"
                                        href="/signup" // Replace with actual signup link
                                        variant="contained"
                                        sx={{
                                            backgroundColor: "#6d8d6e",
                                            color: "white",
                                            padding: "10px 24px",
                                            fontSize: "16px",
                                            borderRadius: "8px",
                                            "&:hover": {
                                                backgroundColor: "#5a7a5b",
                                            },
                                        }}
                                    >
                                        Sign Up to Create Your Community
                                    </Button>
                                </Box>
                            </CardContent>
                        </Card>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
};

export default FeaturesSection;
