import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { Button, Container } from "@mui/material";

// Example cover image import (replace with your actual image)
import coverImage from "../../images/developerCover.png"; // Adjust path accordingly

const BuildOnAlkebuleum = () => {
  return (
    <Box sx={{ py: 10, bgcolor: "white", position: "relative" }} id="buildonalkebuleum">

      <Container maxWidth="lg">
        <Grid container spacing={5} alignItems="center" justifyContent="center">
          {/* Left Side: Cover Image */}
          <Grid item xs={12} md={6}>
            <Box
              component="img"
              src={coverImage}
              alt="Build on Alkebuleum"
              sx={{
                width: "80%",  // Reduced width to 80% as in previous section
                height: "auto",
                borderRadius: "8px",
                boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
              }}
            />
          </Grid>

          {/* Right Side: Content */}
          <Grid item xs={12} md={6}>
            {/* Tag above the title */}
            <Box
              sx={{
                backgroundColor: "rgba(208, 233, 239, 0.5)", // Variation of #D0E9EF with transparency
                color: "#7BA6AC", // Dark grey for text contrast
                px: 3,
                py: 1,
                borderRadius: "20px",
                fontSize: "14px",
                textTransform: "uppercase",
                mb: 2,  // Add margin below the tag
                display: "inline-block", // Make the tag size match text length
              }}
            >
              Developers
            </Box>
            <Typography
              variant="h4"
              component="h2"
              sx={{ fontWeight: "bold", mb: 3, color: "#333", textAlign: { xs: "center", sm: "left" } }}
            >
              Build on Alkebuleum
            </Typography>

            {/* Updated Description Content */}
            <Typography
              variant="body1"
              color="textSecondary"
              sx={{ mb: 4, lineHeight: 1.7, textAlign: { xs: "center", sm: "left" } }}
            >
              Alkebuleum is fully compatible with the Ethereum Virtual Machine (EVM), which means any code written for Ethereum can be used directly on Alkebuleum. Whether you're developing decentralized applications (dApps), smart contracts, or anything else for Ethereum, you can use the same tools, frameworks, and resources to build on Alkebuleum.
            </Typography>

            <Typography
              variant="body1"
              color="textSecondary"
              sx={{ mb: 4, lineHeight: 1.7, textAlign: { xs: "center", sm: "left" } }}
            >
              The only differences you'll need to keep in mind is that Alkebuleum's network ID is 223724. To help you get started, we recommend exploring Ethereum development resources, which can be applied seamlessly on Alkebuleum.
            </Typography>







            {/* Call-to-Action Buttons */}
            <Box sx={{ textAlign: { xs: "center", sm: "left" }, display: 'flex', gap: 2 }}>
              <Button
                component="a"
                href="https://ethereum.org/en/developers/"  // Replace with actual learn more link
                variant="contained"
                sx={{
                  backgroundColor: "#A97855",
                  color: "white",
                  padding: "10px 24px",
                  fontSize: "16px",
                  borderRadius: "8px",
                  "&:hover": {
                    backgroundColor: "#f9d0ba",
                  },
                }}
              >
                Learn More
              </Button>

              <Button
                component="a"
                href="https://ethereum.org/en/developers/docs/"  // Replace with actual documentation link
                variant="outlined"
                sx={{
                  borderColor: "#A97855",
                  color: "#A97855",
                  padding: "10px 24px",
                  fontSize: "16px",
                  borderRadius: "8px",
                  "&:hover": {
                    backgroundColor: "#f9d0ba",
                    borderColor: "#f9d0ba",
                    color: "#333", // Change text color on hover
                  },
                }}
              >
                Documentation
              </Button>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default BuildOnAlkebuleum;
