import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { Button, Container } from "@mui/material";
import openbookArt from "../../images/openbookArt.png"; // Importing your image

const FeaturesSection = () => {
    return (
        <Box sx={{ py: 10, bgcolor: "white" }} id="featuressection">
            <Container maxWidth="lg">
                <Grid container spacing={5} alignItems="center" justifyContent="flex-start">
                    {/* Left Side: Image Section */}
                    <Grid item xs={12} md={6}>
                        <Box
                            component="img"
                            src={openbookArt}
                            alt="Openbook Art"
                            sx={{
                                width: "75%",  // Reduced the width for better alignment
                                height: "auto",
                                borderRadius: "8px",
                                mx: "auto", // Center the image horizontally
                                display: "block",
                                marginLeft: "0", // Pushing the image further to the left
                            }}
                        />
                    </Grid>

                    {/* Right Side: Text and Call-to-Action Section */}
                    <Grid item xs={12} md={6}>
                        {/* Section Title */}
                        <Typography
                            variant="h4"
                            component="h2"
                            sx={{ fontWeight: "bold", mb: 3, textAlign: "center" }}
                        >
                            Create Your Decentralized Community with Openbook
                        </Typography>

                        {/* Description with more content */}
                        <Typography
                            variant="body1"
                            color="textSecondary"
                            sx={{ mb: 4, textAlign: "center", lineHeight: 1.7 }}
                        >
                            Openbook is a decentralized collaboration platform built on the Alkebuleum blockchain. It allows groups, organizations, and communities to create secure, decentralized workspaces where they can manage projects, communicate, and build together. Openbook leverages blockchain technology to ensure transparency and security, making it the ideal tool for decentralized governance and project management.
                        </Typography>
                        <Typography
                            variant="body1"
                            color="textSecondary"
                            sx={{ mb: 4, textAlign: "center", lineHeight: 1.7 }}
                        >
                            With Openbook, organizations can assign tasks, track progress, and manage decentralized decision-making all in one place. Whether youâ€™re a small group or a large organization, Openbook provides the infrastructure to ensure seamless collaboration in a decentralized way.
                        </Typography>

                        {/* Call-to-Action Button */}
                        <Box sx={{ textAlign: "center" }}>
                            <Button
                                component="a"
                                href="/signup" // Replace with actual signup link
                                variant="contained"
                                sx={{
                                    backgroundColor: "#d2691e", // Orange-brown color
                                    color: "white",
                                    padding: "10px 24px",
                                    fontSize: "16px",
                                    borderRadius: "8px",
                                    "&:hover": {
                                        backgroundColor: "#b25b18",
                                    },
                                }}
                            >
                                Sign Up to Create Your Community
                            </Button>
                        </Box>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
};

export default FeaturesSection;
