import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { Button, Container, Table, TableBody, TableCell, TableContainer, TableRow, Paper } from "@mui/material";

// Example cover image import (replace with your actual image)
import coverImage from "../../images/developerCover.png"; // Adjust path accordingly

const BuildOnAlkebuleum = () => {
  const useCases = [
    "Decentralized Finance (DeFi)",
    "Supply Chain Management",
    "Social Impact Platforms",
    "NFT Marketplaces",
    "Governance & Voting",
  ];

  return (
    <Box sx={{ py: 10, bgcolor: "white", position: "relative" }} id="buildonalkebuleum">
      <Container maxWidth="lg">
        <Grid container spacing={5} alignItems="center" justifyContent="center">
          {/* Left Side: Cover Image */}
          <Grid item xs={12} md={6}>
            <Box
              component="img"
              src={coverImage}
              alt="Build on Alkebuleum"
              sx={{
                width: "80%",  // Reduced width to 80% as in previous section
                height: "auto",
                borderRadius: "8px",
                boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
              }}
            />
          </Grid>

          {/* Right Side: Content */}
          <Grid item xs={12} md={6}>
            {/* Tag above the title */}
            <Box
              sx={{
                backgroundColor: "rgba(208, 233, 239, 0.5)", // Variation of #D0E9EF with transparency
                color: "#7BA6AC", // Dark grey for text contrast
                px: 3,
                py: 1,
                borderRadius: "20px",
                fontSize: "14px",
                textTransform: "uppercase",
                mb: 2,  // Add margin below the tag
                display: "inline-block", // Make the tag size match text length
              }}
            >
              Developers
            </Box>
            <Typography
              variant="h4"
              component="h2"
              sx={{ fontWeight: "bold", mb: 3, color: "#333", textAlign: { xs: "center", sm: "left" } }}
            >
              Build on Alkebuleum
            </Typography>

            {/* Description Content */}
            <Typography
              variant="body1"
              color="textSecondary"
              sx={{ mb: 4, lineHeight: 1.7, textAlign: { xs: "center", sm: "left" } }}
            >
              Alkebuleum provides the perfect platform for developers to create decentralized applications. Explore the possibilities below:
            </Typography>

            {/* Use Cases Table */}
            <TableContainer component={Paper} sx={{ boxShadow: "none", border: "none" }}>
              <Table aria-label="use-cases-table">
                <TableBody>
                  {useCases.map((useCase, index) => (
                    <TableRow key={index}>
                      <TableCell
                        component="th"
                        scope="row"
                        sx={{
                          fontWeight: "bold",
                          fontSize: "16px",
                          borderBottom: "none",
                          color: "#333",
                          py: 2, // Add vertical padding
                          textAlign: { xs: "center", sm: "left" }
                        }}
                      >
                        {useCase}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>

            {/* Call-to-Action Button */}
            <Box sx={{ textAlign: { xs: "center", sm: "left" }, mt: 4 }}>
              <Button
                component="a"
                href="/learn-more"  // Replace with actual learn more link
                variant="contained"
                sx={{
                  backgroundColor: "#A97855",
                  color: "white",
                  padding: "10px 24px",
                  fontSize: "16px",
                  borderRadius: "8px",
                  "&:hover": {
                    backgroundColor: "#f9d0ba",
                  },
                }}
              >
                Learn More
              </Button>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default BuildOnAlkebuleum;
