import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import Stack from "@mui/material/Stack";
import Container from "@mui/material/Container";
import CurrencyBitcoinIcon from "@mui/icons-material/CurrencyBitcoin";
import TravelExploreIcon from "@mui/icons-material/TravelExplore";
import AppRegistrationIcon from "@mui/icons-material/AppRegistration";

const HeroSection = () => {
    return (
        <Box sx={{ py: 0, m: 0, position: "relative" }} id="herosection">
            {/* Background Image */}
            <Box
                component="img"
                src="/back.png"  // Path to the public folder image
                alt="Hero Background"
                sx={{
                    width: "100%",
                    height: { xs: "300px", sm: "500px" },
                    objectFit: "cover",
                    objectPosition: "center",
                    mb: 4,
                    m: 0, // Ensure no margin
                    p: 0, // Ensure no padding
                }}
            />

            <Container maxWidth="lg">
                <Grid container spacing={5} alignItems="center" justifyContent="center">
                    {/* Right Side: Content */}
                    <Grid item xs={12} md={6}>
                        <Typography
                            variant="h4"
                            component="h2"
                            sx={{ fontWeight: "bold", mb: 3, color: "#333", textAlign: { xs: "center", sm: "left" } }}
                        >
                            Build on Alkebuleum
                        </Typography>

                        {/* Description Content */}
                        <Typography
                            variant="body1"
                            color="textSecondary"
                            sx={{ mb: 4, lineHeight: 1.7, textAlign: { xs: "center", sm: "left" } }}
                        >
                            Alkebuleum provides the perfect platform for developers to create decentralized applications that address real-world challenges. Explore the tools and resources available to help you build on Alkebuleum:
                        </Typography>

                        {/* List of Square Buttons */}
                        <Stack direction="row" spacing={2} justifyContent="center" sx={{ mt: 4 }}>
                            {/* Button 1: Get AKE */}
                            <Box
                                sx={{
                                    display: "flex",
                                    flexDirection: "column",
                                    alignItems: "center",
                                    borderRadius: "16px",
                                    backgroundColor: "#f5f5f5",
                                    padding: 2,
                                    width: 150,
                                    height: 150,
                                    textAlign: "center",
                                    boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
                                }}
                            >
                                <CurrencyBitcoinIcon sx={{ fontSize: 40, color: "#6d8d6e", mb: 1 }} />
                                <Typography variant="body1" fontWeight="bold">
                                    Get AKE
                                </Typography>
                            </Box>

                            {/* Button 2: Block Explorer */}
                            <Box
                                sx={{
                                    display: "flex",
                                    flexDirection: "column",
                                    alignItems: "center",
                                    borderRadius: "16px",
                                    backgroundColor: "#f5f5f5",
                                    padding: 2,
                                    width: 150,
                                    height: 150,
                                    textAlign: "center",
                                    boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
                                }}
                            >
                                <TravelExploreIcon sx={{ fontSize: 40, color: "#6d8d6e", mb: 1 }} />
                                <Typography variant="body1" fontWeight="bold">
                                    Block Explorer
                                </Typography>
                            </Box>

                            {/* Button 3: Try Apps */}
                            <Box
                                sx={{
                                    display: "flex",
                                    flexDirection: "column",
                                    alignItems: "center",
                                    borderRadius: "16px",
                                    backgroundColor: "#f5f5f5",
                                    padding: 2,
                                    width: 150,
                                    height: 150,
                                    textAlign: "center",
                                    boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.1)",
                                }}
                            >
                                <AppRegistrationIcon sx={{ fontSize: 40, color: "#6d8d6e", mb: 1 }} />
                                <Typography variant="body1" fontWeight="bold">
                                    Try Apps
                                </Typography>
                            </Box>
                        </Stack>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
};

export default HeroSection;
