import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { Button, Container } from "@mui/material";
import openbookArt from "../../images/openbookArt.png";

const FeaturesSection = () => {
    return (
        <Box sx={{ py: 10, bgcolor: "white" }} id="featuressection">
            <Container maxWidth="lg">
                <Grid container spacing={5} alignItems="center" justifyContent="center">
                    {/* Left Side: Image Section */}
                    <Grid item xs={12} md={6} sx={{ textAlign: "center" }}>
                        <Box
                            component="img"
                            src={openbookArt}
                            alt="Openbook Art"
                            sx={{
                                width: "70%",  // Reduced the width slightly more
                                height: "auto",
                                borderRadius: "8px",
                                mb: 4, // Add more space at the bottom for better separation
                            }}
                        />
                    </Grid>

                    {/* Right Side: Text and Call-to-Action Section */}
                    <Grid item xs={12} md={6}>
                        {/* Section Title */}
                        <Typography
                            variant="h4"
                            component="h2"
                            sx={{ fontWeight: "bold", mb: 2, textAlign: "left" }} // Changed to left align for more readability
                        >
                            Create Your Decentralized Community with Openbook
                        </Typography>

                        {/* Description */}
                        <Typography
                            variant="body1"
                            color="textSecondary"
                            sx={{ mb: 3, textAlign: "left", lineHeight: 1.7 }}  // Left-align for text content
                        >
                            Openbook is the ultimate tool for groups and organizations to build decentralized workspaces. Through
                            collaboration on projects, teams can thrive in a secure, blockchain-powered environment, fostering
                            transparency and growth.
                        </Typography>

                        {/* Additional Details */}
                        <Typography
                            variant="body1"
                            color="textSecondary"
                            sx={{ mb: 3, textAlign: "left", lineHeight: 1.7 }}  // New content
                        >
                            <strong>Key Features:</strong>
                            <ul style={{ marginLeft: '20px', lineHeight: '1.8' }}>
                                <li>Create secure, decentralized communities</li>
                                <li>Collaborate in real-time on important projects</li>
                                <li>Ensure full transparency with blockchain technology</li>
                                <li>Manage tasks, documents, and communications seamlessly</li>
                            </ul>
                        </Typography>

                        {/* Call-to-Action Button */}
                        <Box sx={{ textAlign: "left", mt: 4 }}>
                            <Button
                                component="a"
                                href="/signup" // Replace with actual signup link
                                variant="contained"
                                sx={{
                                    backgroundColor: "#1976d2",  // Updated to a more vibrant blue for better contrast
                                    color: "white",
                                    padding: "12px 28px",
                                    fontSize: "16px",
                                    borderRadius: "8px",
                                    "&:hover": {
                                        backgroundColor: "#1565c0",
                                    },
                                }}
                            >
                                Sign Up to Create Your Community
                            </Button>
                        </Box>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
};

export default FeaturesSection;
