import Grid from "@mui/material/Grid";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { Button, Container } from "@mui/material";
import openbookArt from "../../images/openbookArt.png"; // Importing your image

const FeaturesSection = () => {
    return (
        <Box sx={{ py: 10, bgcolor: "white", position: "relative" }} id="featuressection">
            {/* Tag in the top-right corner */}
            <Box
                sx={{
                    position: "absolute",
                    top: 10,
                    left: 20,
                    backgroundColor: "#B3D9FF", // Pastel blue for a calmer effect
                    color: "#333", // Darker text for contrast
                    px: 3,
                    py: 1,
                    borderRadius: "20px",
                    fontSize: "14px",
                    fontWeight: "bold",
                    textTransform: "uppercase",
                }}
            >
                Go Decentralize
            </Box>

            <Container maxWidth="lg">
                <Grid container spacing={5} alignItems="center" justifyContent="center">
                    {/* Left Side: Image Section */}
                    <Grid item xs={12} md={6}>
                        <Box
                            component="img"
                            src={openbookArt}
                            alt="Openbook Art"
                            sx={{
                                width: "80%",  // Reduced the width to 80%
                                height: "auto",
                                borderRadius: "8px",
                                mx: "auto",
                            }}
                        />
                    </Grid>

                    {/* Right Side: Text and Call-to-Action Section */}
                    <Grid item xs={12} md={6}>
                        {/* Section Title */}
                        <Typography
                            variant="h4"
                            component="h2"
                            sx={{ fontWeight: "bold", mb: 3, textAlign: "center" }}
                        >
                            Create Decentralized Organizations on Alkebuleum with Openbook Dapp
                        </Typography>

                        {/* Description */}
                        <Typography
                            variant="body1"
                            color="textSecondary"
                            sx={{ mb: 4, textAlign: "center", lineHeight: 1.7 }}
                        >
                            Openbook is a decentralized collaboration platform built on the Alkebuleum blockchain. It allows groups, organizations, and communities to create secure, decentralized workspaces where they can manage projects, communicate, and build together. Openbook leverages blockchain technology to ensure transparency and security, making it the ideal tool for decentralized governance and project management.
                        </Typography>

                        {/* Call-to-Action Button */}
                        <Box sx={{ textAlign: "center" }}>
                            <Button
                                component="a"
                                href="/signup" // Replace with actual signup link
                                variant="contained"
                                sx={{
                                    backgroundColor: "#6d8d6e",
                                    color: "white",
                                    padding: "10px 24px",
                                    fontSize: "16px",
                                    borderRadius: "8px",
                                    "&:hover": {
                                        backgroundColor: "#5a7a5b",
                                    },
                                }}
                            >
                                Create Decentralized Organization
                            </Button>
                        </Box>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
};

export default FeaturesSection;
